mapper.select:
SELECT *
FROM oauth2_authorization
WHERE
    state_index_sha256 = #{tokenIndexSha256}
   OR authorization_code_value_index_sha256 = #{tokenIndexSha256}
   OR access_token_value_index_sha256 = #{tokenIndexSha256}
   OR oidc_id_token_value__index_sha256 = #{tokenIndexSha256}
   OR refresh_token_value_index_sha256 = #{tokenIndexSha256}
   OR user_code_value_index_sha256 = #{tokenIndexSha256}
   OR device_code_value_index_sha256 = #{tokenIndexSha256}
