mapper.select:
SELECT p.*
FROM t_role_permission_relation rpr
         JOIN t_permission p ON rpr.permission_id = p.permission_id
WHERE rpr.role_id = #{roleId}
  AND p.deleted = 0;

SELECT p.*
FROM t_permission p
         LEFT JOIN t_role_permission_relation rpr
                   ON p.permission_id = rpr.permission_id AND rpr.role_id = #{roleId}
WHERE rpr.permission_id IS NULL
  AND p.deleted = 0;
