mapper.select:
SELECT r.*
FROM t_user_role_relation urr
         JOIN t_role r ON urr.role_id = r.role_id
WHERE urr.user_id = #{userId}
  AND r.deleted = 0;

SELECT r.*
FROM t_role r
         LEFT JOIN t_user_role_relation urr
                   ON r.role_id = urr.role_id AND urr.user_id = #{userId}
WHERE r.deleted = 0
  AND urr.role_id IS NULL;
